/* site.css
    General CSS, loaded on every page
*/

/* Basics*/
html { box-sizing: border-box; }
*, *:before, *:after { box-sizing: inherit; }
body { font-size: 1em; font-family: "Trebuchet MS", Verdana, Helvetica, Sans-Serif; color: #232323; background-color: #fff; }
header, footer, nav, section { display: block; }

h1, h5, h6, h7 { color: #434343; }
h2, h4, h8 { color: #9a9a99; }
h1 {font-Family:Arial, Helvetica, san-sarif; font-size:22pt;}
h2 {font-Family:Arial, Helvetica, san-sarif; font-size:20pt;}
h3 {font-Family:Arial, Helvetica, san-sarif;font-size:19px;font-weight:500;color: #000000;}
h4 {font-Family: Arial, Helvetica, san-sarif;font-size: 34px;}
h5 {font-Family: Arial, Helvetica, san-sarif;font-size: 29px;}
h6 {font-size: 18px;font-Family: Arial, Helvetica, san-sarif;font-weight: 100;-webkit-font-smoothing: antialiased;}
h7 {font-weight: bold;font-size: 16px;font-Family: Arial, Helvetica, san-sarif;}
h8 {font-weight: bold;font-size: 19px; font-Family: Arial, Helvetica, san-sarif;}
a { font-Family: Arial, Helvetica, san-sarif; font-size: 15px;}
	a:link {font-weight:bold; text-decoration:none;}


/* Styles for basic forms
-----------------------------------------------------------*/
fieldset { border: 1px solid #ddd; padding: 0 1.4em 1.4em 1.4em; margin: 0 0 1.5em 0; }
legend { font-size: 1.2em; font-weight: bold; }
textarea { min-height: 75px; }
.editor-label { margin: 1em 0 0 0; }
.editor-field { margin: 0.5em 0 0 0; }

/* Styles for validation helpers
-----------------------------------------------------------*/
.field-validation-error { color: #ff0000; }
.field-validation-valid, .validation-summary-valid { display: none; }
.input-validation-error { border: 1px solid #ff0000; background-color: #ffeeee; }
.validation-summary-errors, .error { font-weight: bold; color: #ff0000; }
.curatedError { margin: 20px 0; font-size: 1.1em; }

/*------------ Shared Layout Rules ---------------*/
#RenderBodyContainer {margin-left: auto; margin-right: auto; max-width:800px;}
.WideBody {max-width:1100px;}
#ShellBodyContainer {margin-left: auto; margin-right: auto; max-width: 1000px;}
#FooterText{color:#777777;text-align:center;margin-left:auto;margin-right:auto;font-size:12px;}
#LoginContainer{ width:100%; max-width: 800px; margin: 0 auto; display:flex; align-items:flex-end;}
#ShellLoginContainer{ width:100%; max-width: 800px; margin: 0 auto; display:flex; align-items:flex-end;}
#SpecialColor {font-Family: Arial, Helvetica, san-sarif; font-size: 29px; font-weight:bold; margin-top:-1.25em;}
.darkGray {color: #222222;font-Family: Arial, Helvetica, san-sarif; font-size: 19px; font-weight:bold; margin-top:0; text-align:center;}
#LogoffButton { background-repeat: repeat; color: #ffffff; border-radius: 10px; width: 120px; height: 26px; text-align: center; font-size: 18px; font-weight: bold; float: right; padding-top: 3px; }
#LanguageSelector { text-align: right; }

/*---------- Shell ---------------- */
#ShellBodyContainer {margin-left: auto; margin-right: auto; max-width: 1000px;}
#ShellHeader { width: 100%; }
.ShellContentContainer{ width:100%; max-width: 1000px; height: 100%; margin: 0 auto; display:flex; align-items:flex-end;}
#ShellMenuBar { background: black; color: white; height: 40px; margin-top: 8px; padding-bottom: 7px;}
#ShellMenuBar a.ShellMenuItem { padding-right: 30px; color: white; font-size: 1em; font-weight: bold; }
#ShellMenuBar a.ShellMenuItem:last-of-type {padding-right: 0;}
#ShellMenuBar a.ShellMenuItem:hover { color: #CCC; }

/*--- Header Logo ---*/
.HeaderLogoImg { max-height: 100px; }


/*------------- General Style & Layout--------------*/
.centered { text-align: center; margin: auto 0; }
img.fullScaled { width: 100%; margin: auto 0; }
.normalText { color: #000000; font-size: 14pt; }
.leftRightPadded { padding: 0 20px; }
.col500 { width: 100%; max-width: 500px; margin: 0 auto; }
.col700 { width: 100%; max-width: 700px; margin: 0 auto; }
.hidden { display: none; }
.clickable:hover { cursor: pointer; }
.flexSpacer { flex: 1 1 auto; }
.grayed { color: #999999; }
input[type="submit"] { cursor: pointer; }
.ui-datepicker-calendar { background: #FFFFFF; }
.sectionTitle { font-size: 28px; font-weight: bold; margin: 0 auto 30px;; }
.sectionHeader { font-size: 24px; font-weight: bold; color: #333; }
.sectionLargeDesc { font-size: 24px; color: #555; }
.sectionDesc { font-size: 20px; color: #555; }

/*------------Responsive Design Forms---------------*/
div.resp_form_dbl { box-sizing: border-box; max-width: 800px; margin: 0 auto; }
    div.resp_form_dbl > div.resp_form_item_subtle { box-sizing: border-box; display: inline-block; width: 50%; padding: 0 30px 0 0; margin-bottom: 8px; }
div.resp_form_item_subtle:nth-of-type(2n) { padding: 0 0 0 30px; }
.resp_form_title_subtle > label { font-weight: bold; color: #808080; }
div.resp_form_title_subtle { box-sizing: border-box; display: inline-block; width: 40%; }
div.resp_form { max-width: 400px; margin: 0 auto; }

div.resp_form_item { box-sizing: border-box; margin-bottom: 8px; }
    div.resp_form_item > label { box-sizing: border-box; display: inline-block; width: 40%; font-weight: bold; color: #808080; padding-right: 6px; }

div.resp_input { display: inline-block; width: 60%; min-width: 200px; }
    div.resp_input > select { max-width: 100%; }
    div.resp_input > input[type="text"], div.resp_input > input[type="password"] { width: 100%; }
div.resp_input_shortened { box-sizing: border-box; display: inline-block; }
    div.resp_input_shortened > input[type="text"] { width: 80%; margin-right: 4px; }

/*------------Unified Responsive Forms for Desktop/Mobile ----------------------------------------*/
div.uni_form { max-width: 360px; margin: 0 auto; text-align: left; }
div.uni_form label { font-weight: bold; font-size: 1.1em; color: #545454; }
    div.uni_form input { width: 100%; height: 38px; color: #808080; }
    div.uni_form input[type=text] { padding-left: 4px; font-size: 18px; }
.uni_questionBox { background-image: url(../Images/QuestionBlock.png); background-repeat: no-repeat; width: 30px; height: 30px; }
.uni_form_item { margin-bottom: 20px; }
.uni_input select { font-size: 20px; padding: 4px; max-width: 360px; }
.uni_form_checkbox_area { display: flex; align-items: center; }
.uni_form_checkbox_area input[type=checkbox] { width: 80px; margin-right: 8px;  }


/*------------!Button Designs!---------------*/
.button, a.button:link, a.button:visited, span.button > a:link, span.button > a:visited {
    display:inline-block;
    margin: 2px 0;
    padding: 4px 15px 4px 15px;
    font-size: 15px;
    font-weight: bold;
    background-size: 100% 100%;
    border-radius: 8px;
    color: white;
    border: none;
}
.largeButton, a.largeButton:link, a.largeButton:visited {
    padding: 6px 18px 6px 18px;
    font-size: 18px;
