}

/*--- Balance on Points and Rewards! ---*/
.balanceContainer { margin: 4px auto; background-size: 100% 100%; color: #FFFFFF; }
.ghost { width: 100%; display: inline-block; position: relative; top: 50%; transform: translateY(-50%); }
.balanceHalf { vertical-align: middle; display: inline-block; width: 50%; padding: 0 4%; text-align: center; }
.balanceValueArea { color: #666666; }
