#cp_sortSelect { font-size: 18px; height: 32px; padding: 0 8px;}
#cp_get_coupon_error { padding: 20px; font-size: 18px; color: white; background: darkred; cursor: pointer; }
#cp_couponMainArea { display: flex; align-items: flex-start; min-height: 210px; background: #EFEFEF; }

#cp_pointBannerArea { margin-bottom: 10px; }
.cp_pointBanner {
    background-image: linear-gradient(
    45deg,
    #cfcf17,
    #b8b814 10%,
    #cfcf17 20%,
    #e6e619 25%,
    #cfcf17 30%,
    #8a8a0f 55%,
    #cfcf17 85%,
    #e6e619 95%,
    #cfcf17
    );
    border: 3px solid #4d4d19;
    text-align: center;
    padding: 18px;
    color: white;
    font-size: 1.6em;
    font-weight: bold;
}
.cp_pointNotice {
    border-left: 2px solid #444449;
    border-right: 2px solid #444449;
    border-bottom: 2px solid #444449;
    padding: 4px;
    background: #E4E4E4;
    font-size: 0.9em;
    color: #444449;
}

.cp_filterSection { flex: 0 0 auto; background: #EEEEEE; padding: 10px; width: 200px; }
.cp_filterHeader { display: flex; align-self: stretch; }
    .cp_filterHeader > span { font-size: 1.2em; font-weight: bold; }
.cp_filter_close { height: 40px; width: 40px; }
.cp_filterTitle { font-size: 1.1em; display: inline-block; text-align:center; width: 100%; background: #DDDDDD; margin-bottom: 4px; }
.cp_filter { font-size: 1em; }
.cp_filter_selected { font-weight: bold; }


#cp_curatedCouponMainArea { display: flex; align-items: flex-start; min-height: 210px; }
#cp_couponsArea { flex: 1 1 auto; display: flex; flex-wrap: wrap; padding-top: 8px; padding-right: 8px; justify-content: space-around; }
#cp_noCouponMsg { text-align: center; padding: 12px; font-size: 32px; color: #555555; font-weight: bold; }
.cp_coupon_outer { min-height: 230px; max-height: 300px; max-width: 430px; flex: 1 1 284px; padding-left: 8px; padding-bottom: 8px; }
.cp_coupon { position: relative; display: flex; flex-direction: column; height: 100%; width: 100%; border: 2px dashed #777777; text-align: center; background: white; }
.cp_coupon_prog {display: none; position: absolute; text-align: center; padding-top: 15%; height: 100%; width: 100%; background: rgba(0, 0, 0, 0.7); z-index: 60; }

.cp_pointCost {
    background-image: linear-gradient(
    45deg,
    #b8b814,
    #a1a112 10%,
    #b8b814 20%,
    #cfcf17 25%,
    #b8b814 30%,
    #8a8a0f 55%,
    #b8b814 85%,
    #cfcf17 95%,
    #b8b814
    );
    text-align: center;
    padding: 4px;
    color: white;
    border-bottom: 2px solid #4d4d19;
}
.cp_pointCostValue { font-size: 1.4em; font-weight: bold;}

.cp_coupon_title { padding: 4px; font-size: 28px; font-weight: bold; }

.cp_coupon_nontitle { flex: 1 1 auto; display: flex; justify-content: space-around; min-height: 0; }

.cp_leftCoupon {
    display: flex;
    flex-direction: column;
    align-items: flex-end;
    flex-shrink: 0;
}
.cp_disclaimerOpener { display: flex; align-items: flex-end; padding-bottom: 4px; }
.cp_tiny { display: inline-block; font-size: 13px; margin-bottom: 4px; margin-right: 4px; }
.cp_magnifying { width: 30px; height: 30px; }

.cp_coupon_main_text {
    flex-shrink: 1;
    width: 100%;
    align-self: stretch;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: flex-start;
    padding: 6px;
    text-align: left;

}
.cp_coupon_brand { flex: 1 0 auto; width: 100%; font-size: 24px; font-weight: bold; }
.cp_coupon_longdesc { position: relative; flex: 0 1 auto; font-size: 13px; max-width: 150px; color: #555555; overflow: hidden; }
.cp_couponFade { position: absolute; left: 0; bottom: 0; right: 0; height: 50px;
                 background: linear-gradient(rgba(255,255,255,0),rgba(255,255,255,1));
}
.cp_coupon_exp { margin: 4px 0; font-size: 12px; font-weight: bold; }
.cp_clip_button { flex-shrink: 0; align-self: stretch; text-align: center; display: inline-block; margin: 3px 0; color: white; height: 36px; font-weight: bold; padding: 8px; border-radius: 10px; }
.cp_clipped_indicator { background: #33cc33; flex-shrink: 0; align-self: stretch; text-align: center; display: inline-block; margin: 3px 0; color: white; height: 36px; font-weight: bold; padding: 8px; border-radius: 10px; }
.cp_noclip_indicator { background: #aaaaaa; flex-shrink: 0; align-self: stretch; text-align: center; display: inline-block; margin: 3px 0; color: white; height: 36px; font-weight: bold; padding: 8px; border-radius: 10px; }



#cp_bottomControlBar { display: flex; justify-content: space-between; align-items: baseline; }
#cp_quality { background: linear-gradient(#FFFFFF, #CCCCCC); padding: 12px; font-size: 16px; font-weight: bold; }

#cp_faq { font-size: 16px; }
.cp_helpLink { font-size: 18px; }
.cp_mainHeader {  display: inline-block; font-size: 20px; font-weight: bold; margin-top: 20px; }
.cp_questionHeader { display: inline-block; font-size: 18px; font-weight: bold; margin: 6px 0 4px; }

#cp_modalOverlay { display: none; position: fixed; top: 0; left: 0; width: 100%; height: 100%; overflow: auto; background-color: rgb(0,0,0); background: rgba(0, 0, 0, 0.7); }
#cp_modalOverlayInner { display: flex; justify-content: space-around; align-items: center; width: 100%; height: 100%; z-index: 25; }
#cp_modalCoupon { display: flex; flex-direction: column; max-width: 400px; background: white; padding: 8px; }
#cp_dynaCoupon .cp_disclaimerOpener, #cp_dynaCoupon .cp_clip_button { display: none; }
#cp_modalX { align-self: flex-end; width: 40px; height: 40px; }
#cp_disclaimerHeader { display: inline-block; margin-top: 12px; font-weight: bold; }
#cp_upcHeader { display: inline-block; margin-top: 12px; font-weight: bold; }

/*-- coupon display --*/
#cp_display #cp_bottomControlBar { justify-content: flex-end; }
a.cp_toLogin { align-self: center; margin: 6px 0; color: white; padding:8px; border-radius: 10px; }

/*-- curated coupons --*/
.noCuratedCoupons {
    background: #EFEFEF;
    padding: 8px 8px 0 8px;
}
.noCuratedCouponsInner {
    text-align: center;
    background: white;
    padding: 12px;
    font-size: 20px;
}
.noCuratedCoupons a {
    font-size: inherit;
}
.cp_login_coupon_outer {
    cursor: pointer;
    background: white;
    height: 100%;
    padding: 8px;
}

/*-- Custom Layout (Curated+) --*/
#LayoutCustomBody {
    height: 100%;
    margin: 0;
    padding: 0;
}
#LayoutCustomContainer {
    min-height: 100vh;
    background: radial-gradient( #EADAB9, #CAB99D );
}
#LayoutCustomHeader {
    position: relative;
}
.LayoutCustomLogo {
    height: 120px;
}
.Stripe {
    position: absolute;
    width: 100%;
    z-index: 1;
}
#Stripe1 {
    height: 20px;
    top: 44px;
}
#Stripe2 {
    height: 32.4px;
    top: 64px;
}
.LayoutCustomLogoArea {
    position: relative;
    z-index: 2;
}
#LayoutCustomFooterArea {
    margin-top: 40px;
    display: flex;
    flex-wrap: wrap;
}
#LayoutCustomFooterBannerArea {
    flex: 1 1 0px;
    display: flex;
    flex-direction: column;
    justify-content: space-around;
    align-items: center;
}
#LayoutCustomFooterSideArea {
    width: 390px;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
}
.LayoutCustomMoreSavings {
    border: 7px solid white;
    background-color: #0c4d0a;
    background-image: url('../moneyicon.png');
    background-repeat: no-repeat;
    background-size: contain;
    background-position: bottom right;
    padding: 8px;
    color: white;
    font-weight: bold;
    font-size: 30px;
}
.MoreSavingSide {
    margin: 0 0 16px 24px;
    height: 230px;
    width: 360px;
}
.MoreSavingBanner {
    margin-right: 10px;
    margin-left: 10px;
    height: 180px;
    width: 100%;
}
.BottomRightDecor {
    height: 300px;
    width: auto;
}
.LayoutCustomBannerLinkArea {
    width: 100%;
}
.LayoutCustomClientBanner {
    width: 100%;
    margin-bottom: 20px;
}
.flexSpacer {
    flex: 1 1 0px;
}

